import React, { useState } from 'react';
import axios from 'axios';

export function ChatUI() {
  const [input, setInput] = useState('');
  const [chat, setChat] = useState([]);
  const [file, setFile] = useState(null);

  const handleSend = async () => {
    if (!input.trim()) return;
    const res = await axios.post('http://localhost:8000/chat', { message: input });
    setChat((prev) => [...prev, { type: 'user', text: input }, { type: 'bot', text: res.data.reply }]);
    setInput('');
  };

  const handleSearch = async () => {
    const res = await axios.post('http://localhost:8000/search', { query: input });
    setChat((prev) => [...prev, { type: 'user', text: input }, { type: 'bot', text: res.data.summary }]);
    setInput('');
  };

  const handlePdfUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('pdf', file);
    const res = await axios.post('http://localhost:8000/upload-pdf', formData);
    setChat((prev) => [...prev, { type: 'bot', text: res.data.answer }]);
  };

  return (
    <div className="max-w-3xl mx-auto p-4 bg-white rounded-xl shadow-md">
      <div className="h-[400px] overflow-y-auto border p-3 mb-4 rounded bg-gray-50">
        {chat.map((c, idx) => (
          <div key={idx} className={`mb-2 ${c.type === 'user' ? 'text-right' : 'text-left'}`}>
            <span className={`inline-block px-3 py-2 rounded ${c.type === 'user' ? 'bg-blue-100 text-blue-800' : 'bg-gray-200 text-gray-800'}`}>{c.text}</span>
          </div>
        ))}
      </div>

      <div className="flex gap-2 mb-2">
        <input
          type="text"
          className="flex-grow border rounded px-3 py-2"
          placeholder="Nh·∫≠p n·ªôi dung ho·∫∑c c√¢u h·ªèi..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button onClick={handleSend} className="bg-blue-600 text-white px-4 py-2 rounded">G·ª≠i</button>
        <button onClick={handleSearch} className="bg-gray-600 text-white px-4 py-2 rounded">üîç</button>
      </div>

      <div className="flex gap-2 items-center">
        <input type="file" accept="application/pdf" onChange={(e) => setFile(e.target.files[0])} />
        <button onClick={handlePdfUpload} className="bg-purple-600 text-white px-4 py-2 rounded">Upload PDF</button>
      </div>
    </div>
  );
}
