import React, { useState } from 'react';
import axios from 'axios';

export function SearchBox() {
  const [url, setUrl] = useState('');
  const [summary, setSummary] = useState('');

  const handleSearch = async () => {
    if (!url.trim()) return;
    const res = await axios.post('http://localhost:8000/search', { query: url });
    setSummary(res.data.summary);
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">Tóm tắt nội dung từ bài viết</h2>
      <input
        type="text"
        className="w-full p-2 border rounded"
        placeholder="Dán link bài viết..."
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />
      <button
        className="mt-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        onClick={handleSearch}
      >
        Tóm tắt
      </button>
      <div className="mt-2 bg-gray-100 p-2 rounded text-sm whitespace-pre-line">{summary}</div>
    </div>
  );
}
