import React, { useState } from 'react';
import axios from 'axios';

export function ChatUI() {
  const [input, setInput] = useState('');
  const [chat, setChat] = useState([]);
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;

    let res;
    if (filename) {
      const formData = new FormData();
      formData.append('filename', filename);
      formData.append('question', input);
      res = await axios.post('http://localhost:8000/ask-pdf', formData);
      setChat((prev) => [...prev, { type: 'user', text: input }, { type: 'bot', text: res.data.answer }]);
    } else {
      res = await axios.post('http://localhost:8000/chat', { message: input });
      setChat((prev) => [...prev, { type: 'user', text: input }, { type: 'bot', text: res.data.reply }]);
    }

    setInput('');
  };

  const handlePdfUpload = async (file) => {
    const formData = new FormData();
    formData.append('pdf', file);
    const res = await axios.post('http://localhost:8000/upload-pdf', formData);
    setFilename(res.data.filename);
    setChat((prev) => [...prev, { type: 'bot', text: `üìé ƒê√£ t·∫£i l√™n file: ${res.data.filename}` }]);
  };

  return (
    <div className="max-w-3xl mx-auto p-4 bg-white rounded-xl shadow-md">
      <div className="h-[400px] overflow-y-auto border p-3 mb-4 rounded bg-gray-50">
        {chat.map((c, idx) => (
          <div key={idx} className={`mb-2 ${c.type === 'user' ? 'text-right' : 'text-left'}`}>
            <span className={`inline-block px-3 py-2 rounded ${c.type === 'user' ? 'bg-blue-100 text-blue-800' : 'bg-gray-200 text-gray-800'}`}>{c.text}</span>
          </div>
        ))}
      </div>

      <div className="flex gap-2 mb-2 items-center">
        <input
          type="text"
          className="flex-grow border rounded px-3 py-2"
          placeholder={filename ? "H·ªèi v·ªÅ n·ªôi dung file PDF..." : "Nh·∫≠p c√¢u h·ªèi..."}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button onClick={handleSend} className="bg-blue-600 text-white px-4 py-2 rounded">G·ª≠i</button>
      </div>

      <div className="mt-3">
        <label className="block w-full cursor-pointer">
          <input
            type="file"
            accept="application/pdf"
            className="hidden"
            onChange={(e) => {
              const f = e.target.files[0];
              setFile(f);
              handlePdfUpload(f);
            }}
          />
          <div className="w-full p-2 text-center border border-dashed border-purple-400 rounded hover:bg-purple-100 transition">
            üìé Click ƒë·ªÉ ch·ªçn file PDF ƒë·ªÉ h·ªèi
          </div>
        </label>
      </div>
    </div>
  );
}
