import React, { useState } from 'react';
import axios from 'axios';

export function PdfUploader() {
  const [file, setFile] = useState(null);
  const [answer, setAnswer] = useState('');

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('pdf', file);

    const res = await axios.post('http://localhost:8000/upload-pdf', formData);
    setAnswer(res.data.answer);
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">Tìm thông tin từ PDF</h2>
      <input
        type="file"
        accept="application/pdf"
        className="block w-full text-sm mb-2"
        onChange={(e) => setFile(e.target.files[0])}
      />
      <button
        className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
        onClick={handleUpload}
      >
        Upload & Hỏi
      </button>
      <div className="mt-2 bg-gray-100 p-2 rounded text-sm whitespace-pre-line">{answer}</div>
    </div>
  );
}
